import React from "react";
import Bus from "./Test";

class FilterItem extends React.Component {
  handleFilterclick = () => {};
  constructor(props) {
    super(props);
    //console.log(this.props.filter);
  }
  componentDidMount = () => {
    this.setState({
      filter: this.props.filter,
      id: this.props.id,
      handleFilterSelected: this.props.handleFilterClick
    });
  };

  render() {
    let myBus = new Bus(4, 4);

    return (
      <li>
        <input
          type="radio"
          name="category"
          value={this.props.filter.value}
          id={this.props.id}
          checked={this.props.checked}
        />

        <label
          onClick={() => this.props.handleFilterClick(this.props.filter.value)}
          htmlFor="filter0"
        >
          {this.props.filter.name} ({myBus.getTotalWheels(true, back)})
        </label>
      </li>
    );
  }
}

export default FilterItem;

