import React from "react";

class FilterItem extends React.Component {
  constructor(props) {
    super(props);
    this.handleFilterClick = this.handleFilterClick.bind(this);
  }
  componentDidMount = () => {
    console.log(this.props.filters);
    this.setState({
      filter: this.props.filter,
      id: this.props.id,
      handleFilterSelected: this.handleFilterClick
    });
  };

  render() {
    return (
      <li>
        <input
          type="radio"
          name="category"
          value={this.props.filter.value}
          id={this.props.id}
          defaultChecked
        />
        <label
          onClick={() =>
            this.props.handleFilterSelected(this.props.filter.value)
          }
          htmlFor="filter0"
        >
          {this.props.filter.name}
        </label>
      </li>
    );
  }
}

export default FilterItem;

