import React from "react";
import FilterItem from "./FilterItem";

class Filters extends React.Component {
  handleFilterClick = () => {};
  constructor(props) {
    super(props);
    ilterClick = this.props.handleFilterClick.bind(this);
  }

  componentDidMount = () => {
    this.setState({
      filters: this.props.filters
    });
  };

  render() {
    //console.log("logx:" + this.props.handleFilterClick);
    return (
      <section id="filterCategories">
              
        <ul className="filters">
          {this.props.filters.map((filter, i) => (
            <FilterItem
              key={i}
              id={i}
              filter={filter}
              handleFilterClick={this.props.handleFilterClick}
              checked={filter.name == this.props.currentlySelectedFilter}
            />
          ))}
        </ul>
              
        <form id="newCat" className="cat-new">
                  
          <input
            type="text"
            name="item"
            id="itemName"
            className="form-component inpt"
            placeholder="New Category"
          />
                
        </form>
            
      </section>
    );
  }
}

export default Filters;

